mapper.select:
SELECT idEmpresa, idDireccion, nombreEmpresa, nombreComercial, nombreRepresentante, codigoPostal, ciudad, telefono, paginaWeb, rfc, estatus 
FROM empresa 
WHERE nombreEmpresa=#{nombreEmpresa} OR nombreComercial=#{nombreEmpresa}

SELECT idEmpresa, idDireccion, nombreEmpresa, nombreComercial,  nombreRepresentante, codigoPostal, ciudad, telefono, paginaWeb, rfc, estatus 
FROM empresa 
WHERE nombreRepresentante=#{nombreRepresentante}

SELECT idEmpresa, idDireccion, nombreEmpresa, nombreComercial,  nombreRepresentante, codigoPostal, ciudad, telefono, paginaWeb, rfc, estatus 
FROM empresa 
WHERE rfc = #{rfc}

SELECT idEmpresa, nombreEmpresa, nombreComercial, nombreRepresentante, TO_BASE64(logoEmpresa) AS 'logoEmpresaBase64' 
FROM empresa 
WHERE idEmpresa =#{idEmpresa}

mapper.update:
UPDATE empresa 
SET idDireccion=#{idDireccion}, nombreEmpresa=#{nombreEmpresa}, nombreComercial=#{nombreComercial}, nombreRepresentante=#{nombreRepresentante}, codigoPostal=#{codigoPostal}, 
ciudad=#{ciudad}, telefono=#{telefono}, paginaWeb=#{paginaWeb}, estatus=#{estatus} 
WHERE idEmpresa = #{idEmpresa}

UPDATE empresa 
SET logoEmpresa = #{logoEmpresa} 
WHERE idEmpresa = #{idEmpresa}
